// src/App.js

import React, { Component } from "react";
import { Slider } from '@material-ui/core';
import Moment from './moment';
const axios = require('axios').default;

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: {"value": 0}
    }

    this.momentList = new Array();
    this.sliderChangeTime = 0;

    this.value = 0;
    this.setValue = 0;

    this.eventSource = new EventSource("http://localhost:5000/events");
  }

  getUniqueID() {
	const s4 = () => Math.floor((1 + Math.random()) * 0x10000).toString(16).substring(1);
	return s4() + s4() + '-' + s4();
  };

  componentDidMount() {
    this.eventSource.onmessage = e => {
      this.updateNumber(JSON.parse(e.data).value);
    }
  }

  updateNumber(value) {
    this.value = value;
    document.getElementById("value").innerHTML = this.value;
  }

  sendChange(momentList) {
	const response = axios.post(
	  "http://localhost:5000/moments",
	  { value: momentList },
	  { headers: 
	  	{ 'Content-Type': 'application/json'}}
    );
  }

  render() {
	const sliderStyle = {
	  'marginLeft': '25%',
	  'marginRight': '25%',
	};
    return (
      <div className="App">
        <center>
          <h1 id="value">
          	{this.value}
          </h1>
          <div style={sliderStyle}>
          	<Slider id="slider" 
          	  onChange={ (e, val) => {
          		  this.updateNumber(val);
          		  if ((new Date().getTime()) - this.sliderChangeTime > 50) {
          			  this.momentList.push(new Moment(val)); 
            		  this.sliderChangeTime = (new Date()).getTime();
          		  }

          	  } }
          	  onChangeCommitted={ (e, val) => { 
          		  this.momentList.push(new Moment(val));
          		  this.sendChange(this.momentList);
          		  console.log(this.momentList);
          		  console.log(this.momentList[0].time);
          		  this.momentList = [];
          	  } }
          	  min={0}
              max={100}
          	  value={this.value}
          	/>
          </div>
        </center>
      </div>
    );
  }
}

export default App;
