// src/App.js

import React, { Component } from "react";
import { Slider } from '@material-ui/core';

class App extends Component {
  constructor(props) {
    super(props);
      this.state = {
	    data: {"randomValue": 0}
      }
//    this.state = {
//      data: getInitialFlightData()
//    };
//
//    this.columns = [
//      {
//        Header: "Origin",
//        accessor: "origin"
//      },
//      {
//        Header: "Flight",
//        accessor: "flight"
//      },
//      {
//        Header: "Arrival",
//        accessor: "arrival"
//      },
//      {
//        Header: "State",
//        accessor: "state"
//      }
//    ];

    this.randomValue = 0;

    this.eventSource = new EventSource("http://localhost:5000/events");
  }

  componentDidMount() {
    this.eventSource.onmessage = e => {
      this.updateNumber(JSON.parse(e.data));
    }
  }

  updateNumber(newData) {
    this.randomValue = newData.randomValue;

    this.setState(Object.assign({}, { data: newData }));
  }

  render() {
	const sliderStyle = {
		'margin-left': '20%',
		'margin-right': '20%',
	};
    return (
      <div className="App">
        <center>
          <h1>
          	{this.randomValue}
          </h1>
          <div style={sliderStyle}>
          	<Slider
          	  min={0}
              max={100}
          	/>
          </div>
        </center>
      </div>
    );
  }
}

export default App;
