// src/App.js

import React, { Component } from "react";
import { w3cwebsocket as W3CWebSocket } from "websocket";
import { Slider } from '@material-ui/core';

import PlayRateSlider from './control-sliders/PlayRateSlider';
import VolumeSlider from './control-sliders/VolumeSlider';

const AudioContext = window.AudioContext || window.webkitAudioContext;
const audioContext = new AudioContext();

const client = new W3CWebSocket('ws://172.18.86.35:5000');

class App extends Component {
	
  constructor(props) {
    super(props);
    this.state = {
    	volume: 0,
    	playRate: 0,
    	pitch: 0,
    };
    
    this.buttonLabel = 'Begin';
    this.source = audioContext.createBufferSource();
    
    this.gainNode = audioContext.createGain();
    this.gainNode.gain.value = this.smoothAlgorithm(this.state.volume);
    this.gainNode.connect(audioContext.destination);
    
//    this.source.playbackRate.value = this.smoothAlgorithm(this.state.playRate);
    
    var url = "http://localhost:5001/aaaaa.mp3"
    var request = new XMLHttpRequest();
    request.open("GET", url, true);
    request.responseType = "arraybuffer";
    var a = this;
    
    request.onload = () => {
      console.log(request.response);

      var promise = audioContext.decodeAudioData(request.response).then(function (data) {
    	console.log(data);
    	a.source.buffer = data;
    	a.source.loop = true;
    	a.source.connect(a.gainNode);
    	a.source.start(0);
      }, (e) => { console.log(e); });
      console.log(promise);
    }
    
    request.send();
    
    this.playing = false;
    
    this.togglePlay = this.togglePlay.bind(this);
  }
  
  smoothAlgorithm(val) {
	  return Math.pow((val / 100), (Math.log(10) / Math.log(2)));
  }
  
  togglePlay() {
	  this.playing = !this.playing;
	  this.playSoundLoop();
  }
  
  playSoundLoop() {
	var elem = document.getElementById("toggleButton");
	if (this.playing) {
		this.gainNode.gain.value = this.smoothAlgorithm(this.state.volume);
		audioContext.resume();
		elem.innerHTML = 'Stop';
	}
	else {
		audioContext.suspend();
		elem.innerHTML = 'Start';
	}
  }
  
  componentDidMount() {
	  client.onopen = () => {
	   console.log('WebSocket Client Connected');
	  };
	  client.onmessage = (message) => {
	    const dataFromServer = JSON.parse(message.data);
	    this.setState({volume: dataFromServer.volume});
	    this.setState({ playRate: dataFromServer.playRate });
	  };
	}  
  
  render() {
	const sliderStyle = {
	  'marginLeft': '25%',
	  'marginRight': '25%',
	};
    return (
      <div className="App">
        <center>
          <VolumeSlider gainNode={this.gainNode} volume={this.state.volume} client={client}/>
          <PlayRateSlider source={this.source} playRate={this.state.playRate} client={client}/>
          <button id="toggleButton" type="button" onClick={(e, val) => { this.togglePlay(); }}>Begin</button>
        </center>
      </div>
    );
  }
}

export default App;
