// src/App.js

import React, { Component } from "react";
import { Slider } from '@material-ui/core';
const axios = require('axios').default;

class App extends Component {
  constructor(props) {
    super(props);
      this.state = {
	    data: {"randomValue": 0}
      }
//    this.state = {
//      data: getInitialFlightData()
//    };
//
