//      }
//    ];

    this.randomValue = 0;

    this.eventSource = new EventSource("http://localhost:5000/events");
  }

  componentDidMount() {
    this.eventSource.onmessage = e => {
      this.updateNumber(JSON.parse(e.data));
    }
  }

  updateNumber(newData) {
    this.randomValue = newData.randomValue;
  }

  sendChange(value) {
	this.randomValue = value;
	const response = axios.post(
	  "http://localhost:5000/events",
	  { value: value },
	  { headers: { 'Content-Type': 'application/json' }}
    );
  }

  render() {
	const sliderStyle = {
	  'marginLeft': '20%',
	  'marginRight': '20%',
	};
    return (
      <div className="App">
        <center>
          <h1>
          	{this.randomValue}
          </h1>
          <div style={sliderStyle}>
          	<Slider 
          	  onChange={ (e, val) => this.sendChange(val) }
          	  min={0}
              max={100}
          	/>
          </div>
        </center>
      </div>
    );
  }
}

export default App;
