// src/App.js

import React, { Component } from "react";
import { w3cwebsocket as W3CWebSocket } from "websocket";
import { Slider } from '@material-ui/core';
import Moment from './moment';
const axios = require('axios').default;

const client = new W3CWebSocket('ws://127.0.0.1:5000');

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
    	value: 0
    };

  }

  onEditorStateChange() {
    client.send(JSON.stringify(this.state));
  }

  componentDidMount() {
	  client.onopen = () => {
	   console.log('WebSocket Client Connected');
	  };
	  client.onmessage = (message) => {
	    const dataFromServer = JSON.parse(message.data);
	    this.handleChange(dataFromServer);
	    //this.updateNumber(dataFromServer.value);
	  };
	}

//  componentDidMount() {
//    this.eventSource.onmessage = e => {
//      this.updateNumber(JSON.parse(e.data).value);
//    }
//  }


  handleChange(event, value) {
	  this.setState({ value: value });
  }

  updateNumber(value) {
	this.value = value;
    document.getElementById("value").innerHTML = this.value;
  }

  sendChange(momentList) {
	const response = axios.post(
	  "http://localhost:5000/moments",
	  { value: momentList },
	  { headers: 
	  	{ 'Content-Type': 'application/json'}}
    );
  }

  render() {
	const sliderStyle = {
	  'marginLeft': '25%',
	  'marginRight': '25%',
	};
    return (
      <div className="App">
        <center>
          <h1 id="value">
          	{this.state.value}
          </h1>
          <div style={sliderStyle}>
          	<Slider id="slider" 
          	  onChange={this.handleChange}
          	  min={0}
              max={100}
          	  value={this.state.value}
          	/>
          </div>
        </center>
      </div>
    );
  }
}

export default App;
