// src/App.js

import React, { Component } from "react";
import { Slider } from '@material-ui/core';
import Moment from './moment';
const axios = require('axios').default;

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: {"value": 0}
    }

    this.momentList = new Array();
    this.sliderChangeTime = 0;

    this.value = 0;
    this.setValue = 0;

    this.eventSource = new EventSource("http://localhost:5000/events");
  }

  componentDidMount() {
    this.eventSource.onmessage = e => {
      this.updateNumber(JSON.parse(e.data));
    }
  }

  updateNumber(newData) {
    this.value = newData.randomValue;
  }

  sendChange(value) {
	this.value = value;
	const response = axios.post(
	  "http://localhost:5000/events",
	  { value: value },
	  { headers: { 'Content-Type': 'application/json' }}
    );
  }

  render() {
	const sliderStyle = {
	  'marginLeft': '20%',
	  'marginRight': '20%',
	};
    return (
      <div className="App">
        <center>
          <h1>
          	{this.value}, {this.setValue}
          </h1>
          <div style={sliderStyle}>
          	<Slider 
          	  onChange={ (e, val) => {
          		  this.value = val;
          		  this.setValue = val;
          		  console.log(this.setValue);
          		  if ((new Date().getTime()) - this.sliderChangeTime > 50) {
          			  this.momentList.push(new Moment(val)); 
            		  this.sliderChangeTime = (new Date()).getTime();
          		  }

          	  } }
          	  onChangeCommitted={ (e, val) => { 
          		  this.setValue = val;
          		  this.momentList.push(new Moment(val));
          		  console.log(this.momentList);
          		  console.log(this.momentList[0].time);
          		  this.momentList = [];
          	  } }
          	  min={0}
              max={100}
          	/>
          </div>
        </center>
      </div>
    );
  }
}

export default App;
