    console.log(this.randomValue);

    this.setState(Object.assign({}, { data: newData }));
  }

  render() {
    return (
      <div className="App">
        <center>
          <h1>
          	{this.randomValue}
          </h1>
        </center>
      </div>
    );
  }
}

