// src/App.js

import React, { Component } from "react";
import { Slider } from '@material-ui/core';
import Moment from './moment';
const axios = require('axios').default;

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: {"randomValue": 0}
    }

    this.momentList = new Array();

    this.randomValue = 0;

    this.eventSource = new EventSource("http://localhost:5000/events");
  }

  componentDidMount() {
    this.eventSource.onmessage = e => {
      this.updateNumber(JSON.parse(e.data));
    }
  }

  updateNumber(newData) {
    this.randomValue = newData.randomValue;
  }

  sendChange(value) {
	this.randomValue = value;
	const response = axios.post(
	  "http://localhost:5000/events",
	  { value: value },
	  { headers: { 'Content-Type': 'application/json' }}
    );
  }

  render() {
	const sliderStyle = {
	  'marginLeft': '20%',
	  'marginRight': '20%',
	};
    return (
      <div className="App">
        <center>
          <h1>
          	{this.randomValue}
          </h1>
          <div style={sliderStyle}>
          	<Slider 
          	  onChange={ (e, val) => this.momentList.push(new Moment(val)) }
          	  onChangeCommitted={ (e, val) => { this.momentList.push(new Moment(val)); console.log(this.momentList); } }
          	  min={0}
              max={100}
          	/>
          </div>
        </center>
      </div>
    );
  }
}

export default App;
