// src/App.js

import React, { Component } from "react";
import { w3cwebsocket as W3CWebSocket } from "websocket";
import { Slider } from '@material-ui/core';

const client = new W3CWebSocket('ws://172.18.86.35:5000');

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
    	value: 0
    };

    this.playing = false;

    this.handleChange = this.handleChange.bind(this); 
  }

  togglePlay() {
	  this.playing = !this.playing;
	  this.playSoundLoop();
  }

  playSoundLoop() {
	if (!this.playing) {
		let audio = new Audio(require(".\\sounds\\aaaaa.mp3"));

		audio.addEventListener('ended', () => {
			audio.currentTime = 0;
			audio.play();
		}, false);
		var playPromise = audio.play();

	    if (playPromise !== undefined) {
	      playPromise
	        .then(_ => {
	          // Automatic playback started!
	          // Show playing UI.
	          console.log("audio played auto");
	        })
	        .catch(error => {
	          // Auto-play was prevented
	          // Show paused UI.
	          console.log(playPromise);
	          console.log("playback prevented");
	        });
	    }
	    this.playing = true;
	}
  }

  onEditorStateChange(val) {
	this.setState({ value: val }, () => { client.send(JSON.stringify(this.state)); });    
  }

  componentDidMount() {
	  client.onopen = () => {
	   console.log('WebSocket Client Connected');
	  };
	  client.onmessage = (message) => {
	    const dataFromServer = JSON.parse(message.data);
	    this.handleChange(dataFromServer.value);
	  };
	}  

  handleChange(value) {
	  this.setState({ value: value });
  }

  render() {
	const sliderStyle = {
	  'marginLeft': '25%',
	  'marginRight': '25%',
	};
    return (
      <div className="App">
        <center>
          <h1 id="value">
          	{this.state.value}
          </h1>
          <div style={sliderStyle}>
          	<Slider id="slider" 
          	  onChange={(e, val) => {
          		  this.onEditorStateChange(val);
          	  } }
          	  min={0}
              max={100}
          	  value={this.state.value}
          	/>
          </div>
          <button type="button" onClick={(e, val) => { this.togglePlay(); }}>Begin</button>
        </center>
      </div>
    );
  }
}

export default App;
