import React, { Component } from "react";
import { w3cwebsocket as W3CWebSocket } from "websocket";
import { Slider } from '@material-ui/core';
import Moment from './moment';
const axios = require('axios').default;

const client = new W3CWebSocket('ws://127.0.0.1:5000');

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
    	value: 0
    }

    this.momentList = new Array();
    this.sliderChangeTime = 0;

    this.value = 0;
    this.setValue = 0;
  }

  onEditorStateChange(val) {
    client.send(JSON.stringify({
      value: val
    }));
  }

  componentDidMount() {
	  client.onopen = () => {
	   console.log('WebSocket Client Connected');
	  };
	  client.onmessage = (message) => {
	    const dataFromServer = JSON.parse(message.data);
	    this.updateNumber(dataFromServer.value);
	  };
	}

//  componentDidMount() {
//    this.eventSource.onmessage = e => {
//      this.updateNumber(JSON.parse(e.data).value);
//    }
//  }

  updateNumber(value) {
	this.value = value;
    document.getElementById("value").innerHTML = this.value;
  }

  sendChange(momentList) {
	const response = axios.post(
	  "http://localhost:5000/moments",
	  { value: momentList },
	  { headers: 
	  	{ 'Content-Type': 'application/json'}}
    );
  }

  render() {
	const sliderStyle = {
	  'marginLeft': '25%',
	  'marginRight': '25%',
	};
    return (
      <div className="App">
        <center>
          <h1 id="value">
          	{this.value}
          </h1>
          <div style={sliderStyle}>
          	<Slider id="slider" 
          	  onChange={ (e, val) => {
          		  this.updateNumber(val);
          		  this.onEditorStateChange(val);
//          		  this.updateNumber(val);
//          		  if ((new Date().getTime()) - this.sliderChangeTime > 50) {
//          			  this.momentList.push(new Moment(val)); 
//            		  this.sliderChangeTime = (new Date()).getTime();
//          		  }

          	  } }
//          	  onChangeCommitted={ (e, val) => { 
//          		  this.momentList.push(new Moment(val));
//          		  this.sendChange(this.momentList);
//          		  console.log(this.momentList);
//          		  console.log(this.momentList[0].time);
//          		  this.momentList = [];
//          	  } }
          	  min={0}
              max={100}
          	  defaultValue={this.value}
          	  value={this.value}
          	/>
          </div>
        </center>
      </div>
    );
  }
}

export default App;
